client_script:
console.log("test 1");

frappe.web_form.after_load = async () => {
    console.log("test 3");

    let urlParams = new URLSearchParams(window.location.search);
    let hotelRoom = urlParams.get('hotel_room');
    console.log({ hotelRoom });

    if (hotelRoom && !frappe.web_form.get_value('hotel_room')) {
        frappe.web_form.set_value('hotel_room', hotelRoom);

        // Fetch the room price from the "Hotel Room" doctype
        frappe.call({
            method: "frappe.client.get_value",
            args: {
                doctype: "Hotel Room",
                fieldname: "room_rate",
                filters: { name: hotelRoom },
            },
            callback: function(response) {
                if (response.message) {
                    let roomPrice = response.message.room_rate;
                    console.log("Fetched Room Price:", roomPrice);

                    // Set the fetched room price in the form
                    frappe.web_form.set_value("room_price", roomPrice);
                }
            }
        });
    }
};

// Function to calculate net total
function calculate_net_total() {
    let fromDate = frappe.web_form.get_value('from_date');
    let toDate = frappe.web_form.get_value('to_date');
    let roomPrice = frappe.web_form.get_value('room_price');

    if (fromDate && toDate && roomPrice) {
        let start = new Date(fromDate);
        let end = new Date(toDate);

        // Calculate the difference in days
        let days = Math.ceil((end - start) / (1000 * 60 * 60 * 24));

        if (days > 0) {
            let total = days * roomPrice;
            console.log("Calculated Net Total:", total);
            frappe.web_form.set_value("total_days", days);
            frappe.web_form.set_value("net_total", total);
        } else {
            frappe.web_form.set_value("net_total", 0);
        }
    }
}

// Listen for changes in `from_date` and `to_date`
frappe.web_form.on('from_date', calculate_net_total);
frappe.web_form.on('to_date', calculate_net_total);
